export const actionTypes = {
    UPDATE_INFO: 'UPDATE_INFO',
    UPDATE_INFO_SUCCESS: 'UPDATE_INFO_SUCCESS',
    UPDATE_INFO_FAIL: 'UPDATE_INFO_FAIL'
};

export function updateInfo(info){
    return {
        type: actionTypes.UPDATE_INFO,
        email: info.email,
        firstName: info.firstName,
        lastName: info.lastName,
        address: info.address,
        apartment: info.apartment,
        city: info.city,
        postalCode: info.postalCode,
        phone: info.phone,
    }
}

export function updateInfoSuccess(data){
    return {
        type: actionTypes.UPDATE_INFO_SUCCESS,
        data
    }
}

export function updateInfoFail(error){
    return {
        type: actionTypes.UPDATE_INFO_FAIL,
        error
    }
}
