import { all } from 'redux-saga/effects';
import AuthSaga from './auth/saga';

export default function* rootSaga() {
    yield all([
        PostSaga(),
        ProductSaga(),
        SettingSaga(),
        CartSaga(),
        AuthSaga(),
        CompareSaga(),
        WishlistSaga(),
        CollectionSaga(),
        MediaSaga(),
        App(),
        CheckoutSaga(),
        UserSaga(),
        OrderSaga()
    ]);
}
