import React, { useEffect, useState } from 'react';
import { DatePicker, Form, Input, Radio, notification } from 'antd';
import { useDispatch, useSelector } from 'react-redux';

const { TextArea } = Input;

const FormChangeContact = ({ }) => {
    const [form] = Form.useForm();
    const [results, setResults] = useState({});

    const getContact = async () => {
        const res = await fetch('/api/services')
        const json = await res.json()
        form.setFieldsValue({
            services_1: json.data.services_1,
            services_2: json.data.services_2,
            services_3: json.data.services_3,
        });
    }

    const handleSubmit = async (values) => {
        const res = await fetch('/api/services', {
            method: 'post',
            body: JSON.stringify(values)
        })
    };

    useEffect(() => {
        getContact()
    }, [])


    return (

        <Form
            className="ps-form--account-setting"
            onFinish={handleSubmit}
            form={form}>

            <div className="ps-form__content">
                <div className="form-group">

                    <Form.Item
                        name="facebook"
                    >
                        <TextArea
                            className="form-control"
                            rows={4}
                            placeholder="Facebook Link"
                        />
                    </Form.Item>
                </div>
                <div className="form-group">

                    <Form.Item name="instagram" >
                    <TextArea
                            className="form-control"
                            rows={4}
                            placeholder="Facebook Link"
                        />
                    </Form.Item>
                </div>
                <div className="form-group">
                    <Form.Item
                        name="linkedin"
                    >
                        <TextArea
                            className="form-control"
                            rows={4}
                            placeholder="Facebook Link"
                        />
                    </Form.Item>
                </div>


                <div className="form-group">
                    <button className="ps-btn">Update profile</button>
                </div>
            </div>
        </Form>
    );
};

export default FormChangeContact;
